import pandas as pd    ########################### TITANIC# Carga los datos en un DataFrametitanic = pd.read_csv("TitanicEdades.csv", usecols = [0,1,2], header = None, index_col = 0)pd.set_option("display.max_rows", 10)pd.set_option("display.max_columns", None)# Imprime los datos por columnaprint(titanic[1])titanic[2]# Asigna nombres a las columnas.titanic.columns = ["genero", "edad"]# Imprime la información por nombre de columnatitanic.generotitanic["genero"]# Imprime la edad y el género de los pasajeros 321 y 574titanic.loc[[321, 574], ["edad", "genero"]]# Filtra los datos donde solo tengas niños confirmados (<11),titanic [(titanic["edad"] > 0) & (titanic["edad"] < 11) ]# adolescenes (12-17), adultos (18-65), adultos mayores (>65)titanic [(titanic["edad"] > 12) & (titanic["edad"] < 17) ]titanic [(titanic["edad"] > 18) & (titanic["edad"] < 65) ]titanic [(titanic["edad"] > 65) ]# Calcula el porcentaje de hombres y mujeresporcentaje_hombres = ((titanic[ titanic["genero"] == "HOMBRE"]["genero"].size)/(titanic["genero"].size))*100porcentaje_mujeres = ((titanic[ titanic["genero"] == "MUJER"]["genero"].size)/(titanic["genero"].size))*100print("El porcentaje de pasajeros hombres: ", porcentaje_hombres, "%")print("El porcentaje de pasajeros mujeres: ", porcentaje_mujeres, "%")# Enlista los identificadores a todos los pasajeros que tienen la menor edadmenor = titanic["edad"][titanic["edad"]>0].min()titanic["edad"][titanic["edad"] == menor].index# Enlista los identificadores a todos los pasajeros que tienen la mayor edadmayor = titanic["edad"][titanic["edad"]>0].max()titanic["edad"][titanic["edad"] == mayor].index# Calcula la edad promedio (sin tomar en cuenta los registrados con edad 0)titanic["edad"][titanic["edad"]>0].mean()