import pandas as pd#Ejercicios de groupby()df1 = pd.DataFrame({    "compañia" : ["TSL", "TSL", "GME", "GME"],    "precio" : [100, 200, 500, 1000],    "variacion" : [10, 20, 50, 100]    })df1.groupby("compañia")[["precio", "variacion"]].agg(["mean", "min", "max", "std", "count"])df2 = pd.DataFrame({    "compañia" : ["TSL", "TSL", "GME", "GME"],    "division" : ["A", "P", "A", "P"],    "precio" : [100, 200, 500, 1000],    "variacion" : [10, 20, 50, 100]    })df2.groupby(["compañia", "division"])["precio"].mean()df2.groupby(["division", "compañia"])["precio"].mean()print(df1)#Ejerciciosizquierda = pd.DataFrame({    "key" : ["k0", "k1", "k2", "k3"],    "A" : ["A0", "A1", "A2", "A3"],    "B" : ["B0", "B1", "B2", "B3"]    })derecha = pd.DataFrame({    "key" : ["k0", "k1", "k2", "k3"],    "C" : ["C0", "C1", "C2", "C3"],    "D" : ["D0", "D1", "D2", "D3"]    })resultado = pd.merge(izquierda, derecha, on=["key"])print("izquierda\n", izquierda,"\n")print("derecha\n", derecha,"\n")print("resultado\n", resultado,"\n\n\n")