import pandas as pd# Lee los archivos: movies y ratingspeliculas = pd.read_csv("movies.csv")califs = pd.read_csv("ratings.csv")# En promedio_califs calcula los promedios de los# raitings obtenidos por películapromedio_califs = califs.groupby("movieId").mean()del promedio_califs["userId"]del promedio_califs["timestamp"]# Ordena los valores de promedio_califs# de manera descendente, deja el resultado en# promedio_califspromedio_califs.sort_values("rating", ascending = False, inplace = True)# En pelis_califs une peliculas y promedio_califs,# utilizando moviedIdpelis_califs =  pd.merge(peliculas, promedio_califs, on = ["movieId"])# Utilizando pelis_califs crea agrupa_pelis# donde agrupes por título y las ordenes en forma# decreciente con respecto al título.agrupa_pelis = pelis_califs.groupby("title")[["title", "rating"]].mean()agrupa_pelis.sort_values("title", ascending = False, inplace = True)