import pandas as pd"""# Seriesmi_serie1 = pd.Series([1,2,3], index = ["a", "b", "c"])mi_serie2 = pd.Series([4,5,6], index = ["a", "b", "c"])# DataFramesdf = pd.DataFrame()df["serie1"] = mi_serie1df["serie2"] = mi_serie2df["multiplicacion"] = df["serie1"] * df["serie2"]"""datos = {    "nombre": ("Anastasia", "Dima", "Katherine", "James", "Emily", "Michael", "Matthew", "Laura", "Kevin", "Jonas"),     "puntaje":  (12.5, 9, 16.5, 0, 9, 20, 14.5, 0, 8, 19),    "intentos": (1,3,2,3,2,3,1,1,2,1),    "califica": ("si", "no", "si", "no","no","si","si","no","no","si")        }indice = ["a", "b", "c", "d", "e", "f", "g", "h", "i", "j"]# Crea un diccionario y carga los datos en un dataframedf = pd.DataFrame(datos, index=indice)# Obten la transpuesta de los datosprint("La transpuesta es: ", df.transpose())# Obten estadísticas descriptivas de los datosprint("Obten estadísticas descriptivas de los datos", df.describe())# Obten información estructural del dataframeprint("Obten información estructural del dataframe", df.info())# Grafica la información numéricadf.plot()# Obten el número de renglonesprint("Obten el número de renglones: ", df.shape[0])# Obten el número de columnasprint("Obten el número de columnas: ", df.shape[1])# Los primeros tres renglones del DataFrameprint(" Los primeros tres renglones del DataFrame", df[0:3])print(" Los primeros tres renglones del DataFrame", df[:3])print(" Los primeros tres renglones del DataFrame", df["a":"c"])print(" Los primeros tres renglones del DataFrame", df.iloc[[0,1,2]])print(" Los primeros tres renglones del DataFrame", df.iloc[0:3])print(" Los primeros tres renglones del DataFrame", df.iloc[:3])# Los últimos tres renglones del DataFrameprint(" Los últimos tres renglones del DataFrame", df[df.shape[0]-3:])print(" Los primeros tres renglones del DataFrame", df[-3:])# Ordena por puntajedf.sort_values("puntaje", ascending = False, inplace = False)# Ordena por intentosdf.sort_values("intentos")# Ordena por puntaje e intentosdf.sort_values(["puntaje", "intentos"], ascending= [True, False])# Obten la columna "nombre"df["nombre"]# Obten la columna "intentos"df["intentos"]# Obten la columna "nombre" e "intentos"df[["intentos", "nombre"]]# Renglones con intentos mayor o igual a 2print(df[df["intentos"]>=2])# Renglones con puntaje entre 15 y 20print(df[ (df["puntaje"]>=15) & (df["puntaje"] <= 20) ])# Renglones con intentos < 3 y puntaje > 15print(df[(df["intentos"]<3) & (df["puntaje"] > 15) ])# Renglones con intentos < 3 o puntaje > 15df[(df["intentos"]<3) | (df["puntaje"] > 15) ]# Ajusta el puntaje del renglon "h" y "d" a 11.5df.loc[["h", "d"], "puntaje"] = 11.5# Calcula el total de intentos de todos los competidoresprint(df["intentos"].sum())# Calcula el promedio del puntaje de todos los competidoresprint(df["intentos"].mean())# Elimina el renglon "i"df.drop("i")# Ordena por "intentos" y "nombre" de forma descendente y ascendente, respectivamente.# Elimina la columna "intentos"df.pop("intentos")